mapper.update:
UPDATE Submission
uid = #{uid}, type = #{type}, pid = #{pid}, title = #{title}, cover = #{cover}, introduction = #{introduction}, resource = #{resource},
WHERE sid=#{sid}

mapper.select:
SELECT COUNT(*)
FROM SUBMISSION
WHERE pid = #{pid}

SELECT *
FROM Submission

    WHERE pid=#{pid}

ORDER BY submissionTime DESC, sid DESC
LIMIT #{offset}, #{size}

SELECT *
FROM Submission

    WHERE pid=#{pid}

ORDER BY watchTimes DESC, sid DESC
LIMIT #{offset}, #{size}

mapper.update:
UPDATE Submission
SET watchTimes=watchTimes+1
WHERE sid=#{sid}
